package me.samcefalo.sistemaiadbackend.services;import me.samcefalo.sistemaiadbackend.domain.Jogo;import me.samcefalo.sistemaiadbackend.domain.JogoFutebol;import me.samcefalo.sistemaiadbackend.domain.JogoFutsal;import me.samcefalo.sistemaiadbackend.domain.dto.JogoDTO;import me.samcefalo.sistemaiadbackend.services.mappers.JogoMappers;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.ArrayList;import java.util.List;import java.util.stream.Collectors;@Servicepublic class JogoService {    @Autowired    private JogoMappers mappers;    public JogoDTO getJogoDto(Jogo jogo) {        if (jogo instanceof JogoFutsal) {            return mappers.jogoFutsalToJogoFutsalDto((JogoFutsal) jogo);        } else {            return mappers.jogoFutebolToJogoFutebolDto((JogoFutebol) jogo);        }    }    public List<JogoDTO> getListJogoDto(List<Jogo> jogos) {        List<JogoDTO> jogosDtos = new ArrayList<>();        jogosDtos.addAll(jogos.stream().map(acao -> getJogoDto(acao))                .collect(Collectors.toList()));        return jogosDtos;    }}