package me.samcefalo.sistemaiadbackend.services;import me.samcefalo.sistemaiadbackend.domain.Entidade;import me.samcefalo.sistemaiadbackend.domain.Equipe;import me.samcefalo.sistemaiadbackend.domain.Tecnico;import me.samcefalo.sistemaiadbackend.domain.dto.TecnicoDTO;import me.samcefalo.sistemaiadbackend.repositories.TecnicoRepository;import me.samcefalo.sistemaiadbackend.services.exceptions.ObjectNotFoundException;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class TecnicoService {    @Autowired    private TecnicoRepository tecnicoRepository;    public Tecnico find(int id) {        return tecnicoRepository.findById(id).orElseThrow(                () -> new ObjectNotFoundException("Objecto n√£o encontrado. ID: " + id + ", Tipo: " + Entidade.class.getName()));    }    public Tecnico insert(Tecnico tecnico) {        return tecnicoRepository.save(tecnico);    }    public Tecnico update(Tecnico tecnico) {        Tecnico tecnicoNew = find(tecnico.getId());        updateData(tecnicoNew, tecnico);        return tecnicoRepository.save(tecnicoNew);    }    private void updateData(Tecnico tecnicoNew, Tecnico tecnico) {        tecnicoNew.setNome(tecnico.getNome());        tecnicoNew.setEquipe(tecnico.getEquipe());    }    public void delete(int id) {        Tecnico tecnico = find(id);        delete(tecnico);    }    private void delete(Tecnico tecnico) {        tecnicoRepository.delete(tecnico);    }    public List<Tecnico> findAll() {        return tecnicoRepository.findAll();    }    public Tecnico fromDTO(TecnicoDTO tecnicoDTO) {        Tecnico tecnico = new Tecnico();        tecnico.setNome(tecnicoDTO.getNome());        Equipe equipe = new Equipe();        equipe.setId(tecnicoDTO.getId());        tecnico.setEquipe(equipe);        return tecnico;    }}